import React, { Component } from 'react';
import uuidv4 from 'uuid/v4';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

import './App.css';

import Nav from './Components/Nav';
import Task from './Components/Task';
import Input from './Components/Input';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      tasks: [
        {
          todo: "Take Crash Out",
          completed: false,
          id: uuidv4()
        },
        {
          todo: "buy milk",
          completed: false,
          id: uuidv4()
        },
        {
          todo: "make food",
          completed: false,
          id: uuidv4()
        },
      ]
    }

  } 

  handleChecked = (id) => {

    let updatedTasks = this.state.tasks.map( (todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed      
      }
      return todo;
    })
    this.setState({
      task: updatedTasks
    })
  }

  handleAddTask = (task) => {

    let newTask = {
      todo: task,
      completed: false,
      id: uuidv4()
    }

    let currentTaskObj = Object.assign([], this.state.tasks);
    currentTaskObj.push(newTask);
    this.setState({
      tasks: currentTaskObj
    })

  }

  handleUpdateEdit = (id, updatedTask) => {
    let updated = Object.assign([], this.state.tasks);

    updated.map((task) =>
      (task.id === id ? task.todo = updatedTask : task) 
    );

    this.setState({
      tasks: updated
    })

  }

  handleDelete = (id) => {
    let updated = Object.assign([], this.state.tasks);

    let updatedList = updated.filter((task) => 
      task.id !== id  
    );

   this.setState({
     tasks: updatedList
   })

  }

  handleSignUpAndSignIn = (data) => {

    let axiosConfig = {
      headers: {
          'Content-Type': 'application/json;charset=UTF-8',
          "Access-Control-Allow-Origin": "*",
      }
    };

    axios.post('http://localhost:3001/users/register-login', data, axiosConfig)
      .then(result => {
        console.log(result)

        const { token } = result.data;
        console.log(token)
        // Set token to ls
        localStorage.setItem('jwtToken', token);
        // Set token to Auth header

        // Decode token to get user data
        const decoded = jwt_decode(token);

        console.log(decoded)

      })
      .catch(err => {
        console.log(err)
      })
  }

  render() {
    return (
      <div className="App">
        <Nav handleSignUpAndSignIn={this.handleSignUpAndSignIn} />
        <Input handleAddTask={this.handleAddTask} />
        <Task {...this.state} 
              handleChecked={this.handleChecked} 
              handleUpdateEdit={this.handleUpdateEdit}  
              handleDelete={this.handleDelete}
              />
      </div>
    );
  }
}

export default App;
