import React, { Component } from 'react';
import uuidv4 from 'uuid/v4';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

import './App.css';

import Nav from './Components/Nav';
import Task from './Components/Task';
import Input from './Components/Input';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      tasks: [],
      user: null
    }

  } 

  componentDidMount() {

    var token = localStorage.getItem('jwtToken');

    if (token) {

      const currentTime = Date.now() / 1000;
      const decoded = jwt_decode(token);

      if (decoded.exp < currentTime) {
        localStorage.removeItem('jwtToken');
        this.setState({
          user: null
        })
      }
      axios.defaults.headers.common['Authorization'] = token;

      this.setState({
        user: decoded.email
      }, () => {
        this.handleGetData()
      })
    }

  }


  handleChecked = (id) => {

    let updatedTasks = this.state.tasks.map( (todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed      
      }
      return todo;
    })
    this.setState({
      task: updatedTasks
    })
  }

  handleAddTask = (task) => {
    var token = localStorage.getItem('jwtToken');
    const decoded = jwt_decode(token);
    let newTask = {
      todo: task,
      completed: false,
      id: decoded._id
    }

    let axiosConfig = {
      headers: {
          'Content-Type': 'application/json;charset=UTF-8',
          "Access-Control-Allow-Origin": "*",
      }
    };

    axios.post('http://localhost:3001/todo/createtodo', newTask, axiosConfig)
      .then(result => {
        console.log(result.data)
        let currentTaskObj = Object.assign([], this.state.tasks);
        currentTaskObj.push(result.data);
        this.setState({
          tasks: currentTaskObj
        })
      })
      .catch(error => {
        console.log(error)
      })

  }

  handleUpdateEdit = (id, updatedTask) => {
    let updated = Object.assign([], this.state.tasks);

    updated.map((task) =>
      (task.id === id ? task.todo = updatedTask : task) 
    );

    this.setState({
      tasks: updated
    })

  }

  handleDelete = (id) => {
    let updated = Object.assign([], this.state.tasks);

    let updatedList = updated.filter((task) => 
      task.id !== id  
    );

   this.setState({
     tasks: updatedList
   })

  }

  handleGetData = () => {
    axios.get('http://localhost:3001/users/current')
    .then(result => {
      console.log(result)
    })
    .catch(err => {
      console.log(err)
    })
  }

  handleSignUpAndSignIn = (data) => {

    let axiosConfig = {
      headers: {
          'Content-Type': 'application/json;charset=UTF-8',
          "Access-Control-Allow-Origin": "*",
      }
    };

    axios.post('http://localhost:3001/users/register-login', data, axiosConfig)
      .then(result => {


        const { token } = result.data;
        axios.defaults.headers.common['Authorization'] = token;
        // Set token to ls
        localStorage.setItem('jwtToken', token);
        // Set token to Auth header

        // Decode token to get user data
        const decoded = jwt_decode(token);

        this.setState({
          user: decoded.email
        }, () => {
          this.handleGetData()
        })

      })
      .catch(err => {
        console.log(err)
      })
  }

  logout = () => {
    delete axios.defaults.headers.common['Authorization'];
    localStorage.removeItem('jwtToken');
    this.setState({
      user: null
    })
  }

  render() {
    return (
      <div className="App">
        <Nav 
          handleSignUpAndSignIn={this.handleSignUpAndSignIn} 
          user={this.state.user}
          logout={this.logout}
          />
        <Input handleAddTask={this.handleAddTask} />
        <Task {...this.state} 
              handleChecked={this.handleChecked} 
              handleUpdateEdit={this.handleUpdateEdit}  
              handleDelete={this.handleDelete}
              />
      </div>
    );
  }
}

export default App;
